import SwiftUI

struct OficinaItemView: View {
    var nome: String = ""
    var endereco: String = ""
    var telefone1: String = ""
    var telefone2: String = ""
    var imagem: String = ""
    
    var body: some View {
        ZStack{
            Color.secundaria
            HStack{
                imagemOficina
                    .padding(.leading, 8)
                VStack(alignment: .leading, spacing: 5){
                    if let nome = nome {
                        Text(nome)
                    }
                    enderecoView
                    telefones
                }
                .padding(.leading, 5)
                Spacer()
            }
        }
        .frame(width: UIScreen.main.bounds.width - 30, height: 100, alignment: .leading)
        .cornerRadius(25)
        .shadow(color: .black.opacity(0.2), radius: 5, x: 3, y: 3)
        .foregroundColor(.principal)
    }
}

extension OficinaItemView {
    var imagemOficina: some View {
        ZStack{
            Circle()
                .fill(Color.white)
                .shadow(color: .black.opacity(0.1), radius: 2, x: 1, y: 1)
            
            if let imagem = imagem {
                Image(base64String: imagem)!
                    .resizable()
                    .scaledToFit()
                    .frame(width: 70, height: 70)
                    .clipShape(Circle())
            }
        }
        .frame(width: 80, height: 80)
    }
    
    var enderecoView: some View {
        HStack{
            Image(systemName: "map.fill")
                .font(.footnote)
            
            if let endereco = endereco {
                Text(endereco)
                    .font(.footnote)
                    .lineLimit(1)
            }
        }
    }
    
    var telefones: some View {
        HStack{
            if let telefone = telefone1 {
                if telefone != "" {
                    Image(systemName: "phone.fill")
                        .font(.footnote)
                    Text(telefone1)
                        .lineLimit(1)
                }
                
            }
            
            if let telefone = telefone2 {
                if telefone != "" {
                    Image(systemName: "phone.fill")
                        .font(.footnote)
                    Text(telefone2)
                        .lineLimit(1)
                }
            }
        }
    }
}

